pub enum LoggingLevel {

    /// Only shows errors, warnings, and launch information.
    Critical,

    /// Shows everything except debug and trace information.
    Normal,

    /// Shows everything.
    Debug,

    /// Shows nothing.
    Off,
}

pub enum FormItems<'f> {

    #[doc(hidden)]
    Raw {
        string: &'f RawStr,
        next_index: usize,
    },

    #[doc(hidden)]
    Cooked {
        items: &'f [FormItem],
        next_index: usize,
    },
}

pub enum FormParseError<'f> {

    /// The field named `.0` with value `.1` failed to parse or validate.
    BadValue(&'f RawStr, &'f RawStr),

    /// The parse was strict and the field named `.0` with value `.1` appeared
    /// in the incoming form string but was unexpected.
    ///
    /// This error cannot occur when parsing is lenient.
    Unknown(&'f RawStr, &'f RawStr),

    /// The field named `.0` was expected but is missing in the incoming form.
    Missing(&'f RawStr),
}

pub enum FormDataError<'f, E> {

    /// An I/O error occurred while reading reading the data stream. This can
    /// also mean that the form contained invalid UTF-8.
    Io(io::Error),

    /// The form string (in `.0`) is malformed and was unable to be parsed as
    /// HTTP `application/x-www-form-urlencoded` data.
    Malformed(&'f str),

    /// The form string (in `.1`) failed to parse as the intended structure. The
    /// error type in `.0` contains further details.
    Parse(E, &'f str),
}

pub enum Body<T> {

    /// A fixed-size body.
    Sized(T, u64),

    /// A streamed/chunked body, akin to `Transfer-Encoding: chunked`.
    Chunked(T, u64),
}

pub enum Outcome<S, E, F> {

    /// Contains the success value.
    Success(S),

    /// Contains the failure error value.
    Failure(E),

    /// Contains the value to forward on.
    Forward(F),
}

pub enum ConfigError {

    /// The configuration file was not found.
    NotFound,

    /// There was an I/O error while reading the configuration file.
    IoError,

    /// There was an I/O error while setting a configuration parameter.
    ///
    /// Parameters: (io_error, config_param_name)
    Io(io::Error, &'static str),

    /// The path at which the configuration file was found was invalid.
    ///
    /// Parameters: (path, reason)
    BadFilePath(PathBuf, &'static str),

    /// An environment specified in `ROCKET_ENV` is invalid.
    ///
    /// Parameters: (environment_name)
    BadEnv(String),

    /// An environment specified as a table `[environment]` is invalid.
    ///
    /// Parameters: (environment_name, filename)
    BadEntry(String, PathBuf),

    /// A config key was specified with a value of the wrong type.
    ///
    /// Parameters: (entry_name, expected_type, actual_type, filename)
    BadType(String, &'static str, &'static str, Option<PathBuf>),

    /// There was a TOML parsing error.
    ///
    /// Parameters: (toml_source_string, filename, error_description, line/col)
    ParseError(String, PathBuf, String, Option<(usize, usize)>),

    /// There was a TOML parsing error in a config environment variable.
    ///
    /// Parameters: (env_key, env_value, error)
    BadEnvVal(String, String, String),

    /// The entry (key) is unknown.
    ///
    /// Parameters: (key)
    UnknownKey(String),

    /// The entry (key) was expected but was missing.
    ///
    /// Parameters: (key)
    Missing(String),
}

pub enum Environment {

    /// The development environment.
    Development,

    /// The staging environment.
    Staging,

    /// The production environment.
    Production,
}

pub enum Transform<T, B = T> {

    /// Indicates that data should be or has been transformed into the
    /// [`FromData::Owned`] variant.
    Owned(T),

    /// Indicates that data should be or has been transformed into the
    /// [`FromData::Borrowed`] variant.
    Borrowed(B),
}

pub enum LaunchErrorKind {

    /// Binding to the provided address/port failed.
    Bind(hyper::Error),

    /// An I/O error occurred during launch.
    Io(io::Error),

    /// Route collisions were detected.
    Collision(Vec<(Route, Route)>),

    /// A launch fairing reported an error.
    FailedFairings(Vec<&'static str>),

    /// An otherwise uncategorized error occurred during launch.
    Unknown(Box<Error + Send + Sync>),
}

pub enum RouteUriError {

    /// The base (mount point) or route path contains invalid segments.
    Segment,

    /// The route URI is not a valid URI.
    Uri(uri::Error),

    /// The base (mount point) contains dynamic segments.
    DynamicBase,
}
